// ==UserScript==
// @name         SFInfo Fix Contacts
// @namespace    https://it.cornell.edu
// @version      0.11
// @description  Fix contacts in sfinfo
// @author       Holly Klimowicz <hek52@cornell.edu>
// @match        https://sfinfo.cit.cornell.edu/vserver.php*
// @icon         data:image/x-icon;base64,
// @run-at       document-end
// ==/UserScript==


//get a list of table data elements
var tds = document.getElementsByTagName("td");

//create a really ugly regex, sprinkle non-greediness throughout
//does this TD contain something square bracket netid space close square bracket &c &c
//var regex = /(.+?)\[ (.+?) \]\,??/g;
var regex = /(.+?)\[ ([a-zA-Z]+?[0-9]+?) \]\,??/g;

//do replace using aggressive no-bracket one-line for loop
for (var i = 0; i<tds.length; i++)
    tds[i].innerHTML = tds[i].innerHTML.replace(regex, "<a href=\"mailto:$2@cornell.edu\">$1</a>");
